&lt;h1 id=&#34;控制器controller&#34;&gt;&lt;a class=&#34;markdownIt-Anchor&#34; href=&#34;#控制器controller&#34;&gt;#&lt;/a&gt; 控制器 Controller：&lt;/h1&gt;
&lt;pre&gt;&lt;code&gt;@RequestMapping(&amp;quot;index&amp;quot;)
public String selectall(Model model, @RequestParam(value = &amp;quot;pageNum&amp;quot;,defaultValue = &amp;quot;1&amp;quot;) Integer pageNum)&amp;#123;
    PageHelper.startPage(pageNum,5);
    List&amp;lt;Department&amp;gt; list = departmentService.selectAll();
    PageInfo&amp;lt;Department&amp;gt; pageInfo = new PageInfo&amp;lt;&amp;gt;(list);
    model.addAttribute(&amp;quot;pageInfo&amp;quot;,pageInfo);
    return &amp;quot;index&amp;quot;;
&amp;#125;
&lt;/code&gt;&lt;/pre&gt;
