&lt;h1 id=&#34;一-在idea中添加配置文件&#34;&gt;&lt;a class=&#34;markdownIt-Anchor&#34; href=&#34;#一-在idea中添加配置文件&#34;&gt;#&lt;/a&gt; 一、在 IDEA 中添加配置文件&lt;/h1&gt;
&lt;p&gt;package com.lf.config;&lt;/p&gt;
&lt;p&gt;import org.springframework.context.annotation.Configuration;&lt;br&gt;
import org.springframework.web.servlet.config.annotation.CorsRegistry;&lt;br&gt;
import org.springframework.web.servlet.config.annotation.WebMvcConfigurer;&lt;/p&gt;
&lt;p&gt;@Configuration&lt;br&gt;
public class Config implements WebMvcConfigurer {&lt;/p&gt;
&lt;pre&gt;&lt;code&gt;@Override
public void addCorsMappings(CorsRegistry registry) &amp;#123;
    registry.addMapping(&amp;quot;/**&amp;quot;)
            .allowedOrigins(&amp;quot;*&amp;quot;)
            .allowedMethods(&amp;quot;GET&amp;quot;,&amp;quot;HEAD&amp;quot;,&amp;quot;POST&amp;quot;,&amp;quot;PUT&amp;quot;,&amp;quot;DELETE&amp;quot;,&amp;quot;OPTIONS&amp;quot;)
            .allowCredentials(true)
            .maxAge(3600)
            .allowedHeaders(&amp;quot;*&amp;quot;);
&amp;#125;
&lt;/code&gt;&lt;/pre&gt;
&lt;p&gt;}&lt;/p&gt;
